/**
* GripPipeline class.
* 
* An OpenCV pipeline generated by GRIP.
*/

enum Direction {RIGHT = true,LEFT = false};

class GripPipeline : public frc::VisionPipeline {
	private:
		void resizeImage(cv::Mat &, double , double , int , cv::Mat &);
		void hsvThreshold(cv::Mat &, double [], double [], double [], cv::Mat &);
		void cvErode(cv::Mat &, cv::Mat &, cv::Point &, double , int , cv::Scalar &, cv::Mat &);
		void mask(cv::Mat &, cv::Mat &, cv::Mat &);
		void findContours(cv::Mat &, bool , std::vector<std::vector<cv::Point> > &);
		void filterContours(std::vector<std::vector<cv::Point> > &, double , double , double , double , double , double , double [], double , double , double , double , std::vector<std::vector<cv::Point> > &);
		static bool compareRectAreas(cv::Rect a, cv::Rect b);
		static cv::Point findFarthestPoint(std::vector<cv::Point> contour, Direction dir);
		double distance(cv::Point p1, cv::Point p2);
		double areaInsideRectContour(std::vector<cv::Point> contour);
	public:
		GripPipeline();
		void Process(cv::Mat &source);

		cv::Mat resizeImageOutput;
		cv::Mat hsvThresholdOutput;
		cv::Mat cvErodeOutput;
		cv::Mat maskOutput;
		std::vector<std::vector<cv::Point> > findContoursOutput;
		std::vector<std::vector<cv::Point> > filterContoursOutput;
		std::vector<cv::Rect> rectangles;
		std::vector<std::array<cv::Rect, 2>> rectangle_pairs;
		std::vector<std::array<std::vector<cv::Point>, 2>> contour_pairs;

};

